package org.example.p2pfileshare.model;

public class ConnectedPeerRecord {
    private String peerId;   // tên hoặc id peer (từ protocol)
    private String ip;
    private long lastSeenEpochMillis;

    public ConnectedPeerRecord() { }

    public ConnectedPeerRecord(String peerId, String ip, long lastSeenEpochMillis) {
        this.peerId = peerId;
        this.ip = ip;
        this.lastSeenEpochMillis = lastSeenEpochMillis;
    }

    public String getPeerId() { return peerId; }
    public void setPeerId(String peerId) { this.peerId = peerId; }

    public String getIp() { return ip; }
    public void setIp(String ip) { this.ip = ip; }

    public long getLastSeenEpochMillis() { return lastSeenEpochMillis; }
    public void setLastSeenEpochMillis(long lastSeenEpochMillis) { this.lastSeenEpochMillis = lastSeenEpochMillis; }
}
